import React from 'react';
import ReactDom from 'react-dom';

class GameStats extends React.Component {
	render () {
		const stats = this.props.stats;

		return (
			<div>
				<h1>Game Stats</h1>
				<p>{stats}</p>
			</div>
		);
	}
}

class GameOutput extends React.Component {
	render () {
		const output = this.props.output;

		return (
			<div>
				<h1>Game Output</h1>
				<p>{output}</p>
			</div>
		);
	}
}

class UmpireActions extends React.Component {
	render () {
		return (
			<div>
				<button>+ Inning</button>
				<button>- Inning</button>
				<button>+ Out</button>
				<button>- Out</button>
				<button>Dice Roll</button>
				<button>Reset Inning</button>
			</div>
		);
	}
}

class DefenseAction extends React.Component {
	render () {
		return (
			<div>
				<button>Pitch</button>
				<button>Fielder</button>
				<button>Pick Off</button>
			</div>
		);
	}
}

class OffenseAction extends React.Component {
	render () {
		return (
			<div>
				<button>Hit</button>
				<button>Runner</button>
				<button>Steal</button>
				<button>Hit&Run</button>
			</div>
		);
	}
}

class GameConsole extends React.Component {
	render () {
		return (
			<div>
				<OffenseAction />
				<DefenseAction />
				<UmpireAction />
				<GameOutput output={this.props.output} />
				<GameStats stats={this.props.stats} />
			</div>
		);
	}
}

const STATS = { inning: 1 };
const OUTPUT = { output: "It's a hit!" };

ReactDom.render(<GameConsole output={OUTPUT} stats={STATS} />, document.querySelector('#root'));
