import React from 'react';

const umpireConfig = {
	ball: {
		text: 'Ball!',
		icon: null
	},
	foulBall: {
		text: 'Foul Ball!',
		icon: null
	},
	strike: {
		text: 'Strike!',
		icon: null
	},
	out: {
		text: 'Out!',
		icon: null
	},
	hit: {
		text: 'Hit!',
		icon: null
	},
	batterUp: {
		text: 'Batter Up!',
		icon: null
	}
};

const umpireCall = (pitch) => {
	if (pitch === null) {
		return 'batterUp';
	}
	if (pitch < 2) {
		return 'strike';
	}
	if (pitch < 4) {
		return 'foulBall';
	}
	if (pitch < 6) {
		return 'ball';
	} else {
		return 'hit';
	}
};

const DiceRoll = (props) => {
	const pitch = umpireCall(props.diceRoll);
	const { text } = umpireConfig[pitch];
	console.log(props.diceRoll, text);
	console.log(props);

	return (
		<div>
			<button onClick={() => props.dieRoll(pitch)}>Roll Die</button>
			<div>DiceRoll: {props.diceRoll}</div>
			<div>DiceTotal: {props.diceTotal}</div>
			<div>Balls: </div>
			<div>Strikes: </div>
			<div>Outs: </div>
		</div>
	);
};

export default DiceRoll;
