import React from 'react';
import ReactDom from 'react-dom';
import DiceRoll from './DiceRoll';

class App extends React.Component {
	state = { diceRoll: null, diceTotal: null, balls: null, strikes: null, outs: null };

	randomRoll () {
		return Math.floor(Math.random() * 6) + 1;
	}

	dieRoll = () => {
		const roll = this.randomRoll();
		const total = this.state.diceTotal + roll;
		this.setState({ diceRoll: roll, diceTotal: total });
	};

	// componentDidMount () {
	// 	console.log('component is mounting...');
	// 	const roll = this.randomRoll();
	// 	const total = this.state.diceTotal + roll;
	// 	this.setState({ diceRoll: roll, diceTotal: total });
	// }

	render () {
		return (
			<div>
				<button onClick={this.dieRoll}>Roll Die</button>
				<DiceRoll onClick={this.dieRoll} diceRoll={this.state.diceRoll} diceTotal={this.state.diceTotal} />
			</div>
		);
	}
}

ReactDom.render(<App />, document.querySelector('#root'));
