// Need to check when Outs get to 3, increase Innings
export const outsTracker = (stats) => {
	// check if 3 outs
	// if 3 outs, increase inning by .5
	// if 3 outs, reset balls and strikes

	if (stats.outs === 3) {
		console.log('three outs!');
	} else {
		console.log('Waiting for 3 outs....');
	}
};

// Need to check when Innings get to 10, Extra innings is announced
