const umpIndicator = (gameStats) => {
	const { balls, strikes, outs } = gameStats;
	const update = {
		text: '',
		walk: false,
		strikeout: false
	};

	if (balls === 4) {
		update.text = '4 balls -- Take your base!';
		return update;
	} else if (strikes === 3) {
		update.text = '3 strikes - You are out!';
		return update;
	} else return;
};

export default umpIndicator;
