const umpIndicator = (gameStats) => {
	const { balls, strikes, outs } = gameStats;
	const update = {
		walk: '4 balls -- Take your base!',
		strike: '3 strikes - You are out!'
	};

	if (balls === 4) {
		return update.walk;
	} else if (strikes === 3) {
		return update.strike;
	} else return;
};

export default umpIndicator;
