export const umpIndicator = (gameStats) => {
	const { balls, strikes, outs } = gameStats;
	const update = {
		text: '',
		walk: false,
		strikeout: false
	};

	if (balls === 4) {
		update.text = '4 balls -- Take your base!';
		update.walk = true;
		return update;
	} else if (strikes === 3) {
		update.text = '3 strikes - You are out!';
		update.strikeout = true;
		return update;
	} else return update;
};

export const pitchCount = (pitch, gameStats) => {
	// need access to defenseAction.roll to determine pitch
	// need if statementst to determine pitchCount status and text

	let pitchCall = '';

	if (pitch === 6) {
		state.stats.balls = 0;
		state.stats.strikes = 0;
		pitchCall = 'Hit! Resetting balls and strikes.';
	} else if (pitch < 6 && pitch > 3) {
		state.stats.balls += 1;
		pitchCall = 'Ball!';
	} else if (pitch < 4 && pitch > 1 && state.stats.strikes !== 2) {
		state.stats.strikes += 1;
		pitchCall = 'Foul Ball!';
	} else if (pitch < 4 && pitch > 1) {
		pitchCall = 'Foul Ball!';
	} else if (pitch === 1) {
		state.stats.strikes += 1;
		pitchCall = 'Strike!';
	}
};
