import React from 'react';
import ReactDom from 'react-dom';

class GameStats extends React.Component {
	render () {
		const stats = this.props.stats;

		return (
			<div>
				<h1>Game Stats</h1>
				<p>Inning: {stats.inning}</p>
				<p>Strikes: {stats.strikes}</p>
				<p>Balls: {stats.balls}</p>
				<p>Outs: {stats.outs}</p>
			</div>
		);
	}
}

class GameOutput extends React.Component {
	render () {
		const output = this.props.output;

		return (
			<div>
				<h1>Game Output</h1>
				<p>{output}</p>
			</div>
		);
	}
}

class UmpireActions extends React.Component {
	render () {
		return (
			<div>
				<h1>Umpire Actions</h1>
				<button>+ Inning</button>
				<button>- Inning</button>
				<button>+ Out</button>
				<button>- Out</button>
				<button>Dice Roll</button>
				<button>Reset Inning</button>
			</div>
		);
	}
}

class DefenseActions extends React.Component {
	render () {
		return (
			<div>
				<h1>Defense Actions</h1>
				<button>Pitch</button>
				<button>Fielder</button>
				<button>Pick Off</button>
			</div>
		);
	}
}

class OffenseActions extends React.Component {
	render () {
		return (
			<div>
				<h1>Offense Actions</h1>
				<button>Hit</button>
				<button>Runner</button>
				<button>Steal</button>
				<button>Hit&Run</button>
			</div>
		);
	}
}

class GameConsole extends React.Component {
	render () {
		return (
			<div>
				<OffenseActions />
				<DefenseActions />
				<UmpireActions />
				<GameOutput output={this.props.output} />
				<GameStats stats={this.props.stats} />
			</div>
		);
	}
}

const STATS = { inning: 1, strikes: 2, balls: 3, outs: 2 };
const OUTPUT = [
	"It's a hit!"
];

ReactDom.render(<GameConsole output={OUTPUT} stats={STATS} />, document.querySelector('#root'));
