import React from 'react';
import ReactDom from 'react-dom';
import DiceRoll from './DiceRoll';

class App extends React.Component {
	state = { diceRoll: null, diceTotal: null };

	randomRoll () {
		return Math.floor(Math.random() * 6) + 1;
	}

	dieRoll () {
		const roll = this.randomRoll();
		const total = this.state.diceTotal + roll;
		this.setState({ diceRoll: roll, diceTotal: total });
	}

	componentDidMount () {
		const roll = this.randomRoll();
		const total = this.state.diceTotal + roll;
		this.setState({ diceRoll: roll, diceTotal: total });
	}

	render () {
		return (
			<div>
				<button onClick={this.dieRoll}>Roll Die</button>
				<DiceRoll diceRoll={this.state.diceRoll} diceTotal={this.state.diceTotal} />
			</div>
		);
	}
}

ReactDom.render(<App />, document.querySelector('#root'));
