import React from 'react';
import ReactDom from 'react-dom';

import './style.css';

class GameStats extends React.Component {
	render () {
		const stats = this.props.stats;

		return (
			<div className="ui raised segment container">
				<h1 className="ui header">Game Stats</h1>
				<p>Inning: {stats.inning}</p>
				<p>Strikes: {stats.strikes}</p>
				<p>Balls: {stats.balls}</p>
				<p>Outs: {stats.outs}</p>
			</div>
		);
	}
}

class GameOutput extends React.Component {
	render () {
		const output = this.props.output;

		return (
			<div className="ui raised segment container">
				<h1 className="ui header">Game Output</h1>
				<p>{output.result}</p>
			</div>
		);
	}
}

class UmpireActions extends React.Component {
	render () {
		return (
			<div className="ui column raised">
				<h1 className="ui header">Umpire Actions</h1>
				<button className="ui button">+ Inning</button>
				<button className="ui button">- Inning</button>
				<button className="ui button">+ Out</button>
				<button className="ui button">- Out</button>
				<button className="ui button">Dice Roll</button>
				<button className="ui button">Reset Inning</button>
			</div>
		);
	}
}

class DefenseActions extends React.Component {
	render () {
		return (
			<div className="ui column raised">
				<h1 className="ui header">Defense Actions</h1>
				<button className="ui button">Pitch</button>
				<button className="ui button">Fielder</button>
				<button className="ui button">Pick Off</button>
			</div>
		);
	}
}

class OffenseActions extends React.Component {
	render () {
		return (
			<div className="ui column raised">
				<h1 className="ui header">Offense Actions</h1>
				<button className="ui button">Hit</button>
				<button className="ui button">Runner</button>
				<button className="ui button">Steal</button>
				<button className="ui button">Hit&Run</button>
			</div>
		);
	}
}

class GameConsole extends React.Component {
	render () {
		return (
			<div>
				<GameOutput output={this.props.output} />
				<GameStats stats={this.props.stats} />
				<div className="ui four column grid container">
					<OffenseActions />
					<DefenseActions />
					<UmpireActions />
				</div>
			</div>
		);
	}
}

const STATS = { inning: 1, strikes: 2, balls: 3, outs: 2 };
const OUTPUT = { result: "It's a hit!" };

ReactDom.render(<GameConsole output={OUTPUT} stats={STATS} />, document.querySelector('#root'));
